const {styles, className} =css.resolve`
.sort a:hover {
  color: #3c6e71 !important;
  opacity: 0.75;
}

.sort input {
  padding-bottom: 1px;
  border: 0;
  box-shadow: none;
  background: #f1f1f1;
}

@media only screen and (max-width: 1024px) {
  .sort__brands-count {
    display: none;
  }
}

@media only screen and (max-width: 768px) {
  .sort {
    margin: 0;
    padding: 8px;
    padding-left: 0;
  }
  .sort button {
    color: #3c6e71;
  }
  .sort button:hover {
    background-color: #234361;
  }
}
`
