      <title>{title}</title>
      <meta charSet="utf-8" />
      <meta name="viewport" content="initial-scale=1.0, width=device-width" />
    </Head>

    <div style={fixed ? { position: 'fixed' } : {}}>
      <Header />

      {children}
    </div>

