import React from 'react';
import { Pane } from 'evergreen-ui';
import { BrandCertificates } from './BrandCertificates/BrandCertificates';
import { BrandImage } from './BrandImage/BrandImage';
import { BrandLogo } from './BrandLogo/BrandLogo';
import { BrandDetails } from './BrandDetails/BrandDetails';
import { brandStyles } from './styles.js';

export const Brand = ({
  sys: { id },
  fields: {
    certificates,
    image,
    logo,
    title,
    clothingStyles,
    location,
    price,
    website,
  },
}) => (
  <>
    <Pane
      className="brands__brand"
      key={id}
      display="flex"
      flexDirection="column"
      alignItems="center"
      textAlign="left"
    >
      <BrandImage image={image}>
        <BrandCertificates certificates={certificates} />
      </BrandImage>

      <Pane
        position="relative"
        display="flex"
        flexDirection="row-reverse"
        justifyContent="space-between"
        alignItems="center"
        width="100%"
      >
        <BrandLogo logo={logo} title={title} />
        <BrandDetails
          title={title}
          clothingStyles={clothingStyles}
          location={location}
          price={price}
          website={website}
        />
      </Pane>
    </Pane>
    <style jsx>{brandStyles}</style>
  </>
);
