      <meta name="viewport" content="initial-scale=1.0, width=device-width" />
    </Head>

    <div style={state.showFilters ? { position: 'fixed' } : {}}>
      <Header />

      {children}
    </div>

    {/* <footer>{'I`m here to stay'}</footer> */}
  </>
);
