import css from 'styled-jsx/css';

export const { styles, className } = css.resolve`
  .sort :global(a:hover) {
    color: #3c6e71 !important;
    opacity: 0.75;
  }

  .sort :global(input) {
    padding-bottom: 1px;
    border: 0;
    box-shadow: none;
    background: #f1f1f1;
  }

